public class FirstLoopDemo {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int counter = 0;
		
		while(++counter < 10) {
			System.out.printf("Counter value is %d %n", counter);
		}

		System.out.println("Enter of program");
	}

}
